import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ServicioEmpleadosService } from '../servicio-empleados.service';
import { EmpleadosService } from '../empleados.service';
import { Empleado } from '../empleado.model';

@Component({
  selector: 'app-actualiza-component',
  templateUrl: './actualiza-component.component.html',
  styleUrls: ['./actualiza-component.component.css']
})
export class ActualizaComponentComponent implements OnInit{
  constructor(private router:Router, 
    private miServicio: ServicioEmpleadosService,
    private empleado:EmpleadosService,
    private route:ActivatedRoute){

}
ngOnInit(): void {
this.empleados = this.empleado.empleados;
this.indice = this.route.snapshot.params["id"];

let empleado:Empleado = this.empleado.encontrarEmpleado(this.indice);

this.cuadroNombre = empleado.nombre;
this.cuadroApellido = empleado.apellido;
this.cuadroCargo = empleado.cargo;
this.cuadroSalario = empleado.salario;
}

backHome(){
this.router.navigate(['']);
};

empleados:Empleado[]=[];

actualizaEmpleado(){
let miEmpleado = new Empleado(this.cuadroNombre, this.cuadroApellido, this.cuadroCargo, this.cuadroSalario)
//this.miServicio.muestraMensaje("Nombre del empleado: " + miEmpleado.nombre + " " + miEmpleado.apellido);
this.empleado.actualizarEmpleado(this.indice, miEmpleado);

this.router.navigate(['']); //router permite redirigirte a una ruta especifica
}


cuadroNombre: string = "";
cuadroApellido: string = "";
cuadroCargo: string = "";
cuadroSalario: number = 0;

indice:number;

}
<p style="text-align: center;">Actualizar</p>

<form class="form-group row" >
    <div class="form-group col-md-3" >
      <label for="nombre">Nombre: </label>
      <input type="text" name="nombre" id="nombre" placeholder="Nombre" class="form-control" [(ngModel)]="cuadroNombre">
    </div>
    <div class="form-group col-md-3">
      <label for="apellido">Apellido: </label>
      <input type="text" name="apellido" id="apellido" placeholder="Apellido" class="form-control" [(ngModel)]="cuadroApellido">
    </div>
    <div class="form-group col-md-3">
      <label for="cargo">Cargo: </label>
      <input type="text" name="cargo" id="cargo" placeholder="Cargo" class="form-control" [(ngModel)]="cuadroCargo">
    </div>
    <div class="form-group col-md-3">
      <label for="Salario">salario: </label>
      <input type="text" name="salario" id="salario" placeholder="Salario" class="form-control" [(ngModel)]="cuadroSalario">
    </div>
    <br/>
    <div class="col">
      <button type="submit" class="btn btn-primary" (click)="actualizaEmpleado()" style="margin-top: 35px; margin-right: 50px;">Actualizar</button>
      <button type="submit" class="btn btn-primary" (click)="actualizaEmpleado()" style="margin-top: 35px;">Eliminar</button>
    </div>
  </form>

<div style="text-align: center;">
    <button class="btn btn-primary" (click)="backHome()"> Home</button>
</div>
