import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Empleado } from './empleado.model';

@Injectable({
  providedIn: 'root'
})
export class DataService {

  constructor(private httpClient:HttpClient) {  }

cargarEmpleados(){
  return this.httpClient.get('https://clientes-b0735-default-rtdb.firebaseio.com/datos.json');
}

  guardarEmpleados(empleados:Empleado[]){
    this.httpClient.put("https://clientes-b0735-default-rtdb.firebaseio.com/datos.json", empleados).subscribe(
      reponse => console.log("Se han guardado los registros: "+ reponse),
      error => console.log("Error: " + error)
    );
  }

  actualizarEmpleados(indice:number, empleado:Empleado){
    let  url="https://clientes-b0735-default-rtdb.firebaseio.com/datos/" + indice + ".json";
    this.httpClient.put(url, empleado).subscribe(
      reponse => console.log("Se ha modificado el registro: "+ reponse),
      error => console.log("Error: " + error)
    );
  }
}
