import { Component, OnInit } from '@angular/core';
import { Empleado } from "./empleado.model"
import { ServicioEmpleadosService } from './servicio-empleados.service';
import { EmpleadosService } from './empleados.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'Lista de Empleados';

  constructor(private miServicio: ServicioEmpleadosService,
    private empleado:EmpleadosService){
      //this.empleados = this.empleado.empleados;
  }

  ngOnInit(): void {
    this.empleados = this.empleado.empleados;
  }

  empleados:Empleado[]=[];

  agregarEmpleado(){
    let miEmpleado = new Empleado(this.cuadroNombre, this.cuadroApellido, this.cuadroCargo, this.cuadroSalario)
    //this.miServicio.muestraMensaje("Nombre del empleado: " + miEmpleado.nombre + " " + miEmpleado.apellido);
    this.empleado.agregarEmpleadoServicio(miEmpleado);
  }


  cuadroNombre: string = "";
  cuadroApellido: string = "";
  cuadroCargo: string = "";
  cuadroSalario: number = 0;
}
<!-- A grey horizontal navbar that becomes vertical on small screens -->
<nav class="navbar navbar-expand-sm bg-light">

  <!-- Links -->
  <ul class="navbar-nav">
    <li class="nav-item">
      <a class="nav-link" href="#">Home</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" href="#">Proyectos</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" href="#">Quienes somos</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" href="#">Contacto</a>
    </li>
  </ul>

</nav>
<div class="container" class="col" style="text-align: center;">
  <div>
    <h1>
      {{title}}
    </h1>
    <br/>
    <br/>

    <form class="form-group row" >
      <div class="form-group col-md-3" >
        <label for="nombre">Nombre: </label>
        <input type="text" name="nombre" id="nombre" placeholder="Nombre" class="form-control" [(ngModel)]="cuadroNombre">
      </div>
      <div class="form-group col-md-3">
        <label for="apellido">Apellido: </label>
        <input type="text" name="apellido" id="apellido" placeholder="Apellido" class="form-control" [(ngModel)]="cuadroApellido">
      </div>
      <div class="form-group col-md-3">
        <label for="cargo">Cargo: </label>
        <input type="text" name="cargo" id="cargo" placeholder="Cargo" class="form-control" [(ngModel)]="cuadroCargo">
      </div>
      <div class="form-group col-md-3">
        <label for="Salario">salario: </label>
        <input type="text" name="salario" id="salario" placeholder="Salario" class="form-control" [(ngModel)]="cuadroSalario">
      </div>
      <br/>
      <div class="col">
        <button type="submit" class="btn btn-primary" (click)="agregarEmpleado()" style="margin-top: 35px;">Agregar</button>
      </div>
    </form>
  </div>
</div>
<br/>

<app-empleado-hijo *ngFor="let empleado of empleados; let i=index" [empleadoDeLista] = "empleado" [indice] = "i"> </app-empleado-hijo>
